'use strict';

/**
 * @ngInject
 * @ngdoc object
 * @name app.controllers:HomeCtrl
 * @description Controller for the /home route
 */
module.exports = function($scope, Firebase, $rootScope) {

  /**
   * @desc Create reference to controller scope
   * @type {Object}
   */
  var vm = this;

  // Reassign vm.user upon `user::authchange`
  $scope.$on('user::authchange', function() {
    vm.user = Firebase.user;
    return;
  });

  vm.firebase = Firebase;
  
  // Set initial authentication state
  vm.user = Firebase.user;

  return vm;
};
