'use strict';

window.$ =
window.jQuery = require('jquery');

// Require Angular dependencies
// This will add these dependencies to the global scope
require('angular');
require('angular-route');
require('angularfire');

// Require ngFirebaseAuth
require('../../src/main');

// mount on window for testing
angular
  .module('app', [
    'ngRoute',
    'firebase',
    'ngFirebaseAuth'
  ])
  .config(function($locationProvider, $routeProvider) {

    // Set HTML5 pushState
    $locationProvider.html5Mode(true);

    // Set routes
    return $routeProvider
    .when('/about', {
      templateUrl: 'views/about.html',
      controller: 'AboutCtrl as about'
    })
    .when('/', {
      templateUrl: 'views/home.html',
      controller: 'HomeCtrl as home'
    })
    .otherwise({
      redirectTo: '/'
    });

  })
  .factory('Firebase', require('./firebase'))
  .controller('HomeCtrl', require('./home-controller'))
  .controller('NavCtrl', require('./nav-controller'));
