'use strict';

module.exports = function($scope, Firebase) {

  /**
   * @description Reference to controller
   * @type {Object}
   */
  var vm = this;

  vm.firebase = Firebase;

  /**
   * @description Reference to Firebase user
   * @type {Object}
   */
  vm.user = Firebase.user;

  /**
   * @description Log out user
   */
  vm.onLogOutClick = function() {
    return Firebase.ref.unauth();
  };

  $scope.$on('user::authchange', function() {
    vm.user = Firebase.user;
    return;
  });

};
